import XCTest
@testable import LC_Distribute_candy

class SolutionTests: XCTestCase {
    
    private var sut: Solution!
    
    override func setUp() {
        super.setUp()
        sut = Solution()
    }
    
    func test1() {
        
        let input = [1,1,2,2,3,3]
        
        let output = sut.distributeCandies(input)
        
        XCTAssertEqual(output, 3)
    }
    
    func test2() {
        
        let input = [1,1,2,3]
        
        let output = sut.distributeCandies(input)
        
        XCTAssertEqual(output, 2)
    }
    
    func test3() {
        
        let input = [6,6,6,6]
        
        let output = sut.distributeCandies(input)
        
        XCTAssertEqual(output, 1)
    }
    
